import React from 'react';
import { connect } from 'react-redux';
import { play } from '../redux/actions';
import PropTypes from 'prop-types';

import { ReactComponent as Play } from '../icons/play.svg'
import { ReactComponent as Pause } from '../icons/pause.svg'
import Emoji from './emoji'

import styled from 'styled-components'

const StyledButton = styled.button`
    background-color: ${props => props.theme.primaryColor};
    color: white;
    border-radius: 25px;
    width: 150px;
    height: 50px;
    font-size: 20px;

    &:hover {
        background-color: ${props => props.theme.secondaryColor};
        width: 155px;
        height: 55px;
    }
`

const StyledCover =styled.div`
    left: 0;
    width: 30%;
    display: flex;
    flex-direction: column;
    align-items: center;
`

const StyledHover =styled.div`
    position: relative;
    width: 80%;
    
    >img {
        opacity: ${props => props.playing ? 0.5 : 1};
        display: block;
        width: 100%;
        height: auto;
        transition: .5s ease;
        backface-visibility: hidden;
    }

    >div {
        transition: .5s ease;
        opacity: ${props => props.playing ? 1 : 0};
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        -ms-transform: translate(-50%, -50%);
        text-align: center;
    }

    &:hover {
        >img {
            opacity: 0.5;
        }
        >div {
            opacity: 1;
        }
    }
`

function Cover({ songsNumber, playing, playStop })  {
    return (
        <StyledCover>
            <StyledHover playing={playing} onClick={() => playStop(!playing)}>
                <img src="cover.jpg" alt="cover"/>
                <div>
                    {playing ? <Pause /> : <Play />}
                </div>
            </StyledHover>
            <h3>Your Time Capsule</h3>
            <StyledButton onClick={() => playStop(!playing)}>
                {playing ? 'PAUSE' : 'PLAY'}
            </StyledButton>
            <p>
                {songsNumber} SONGS
            </p>
            <Emoji />
        </StyledCover>
    )
}

Cover.defaultProps = {
    songsNumber: 0
}

Cover.propTypes = {
    songsNumber: PropTypes.number,
    playing: PropTypes.bool.isRequired,
    playStop: PropTypes.func
}

const mapStateToProps = (state) => {
    console.log(state);
    return {
        playing: state.playing,
        songsNumber: state.songs.length
    }
}

export default connect(mapStateToProps, {
    playStop: play
})(Cover);
