import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { css } from 'styled-components';

const StyledSpan = styled.span`
    font-family: tryout-icons;
    font-size: 24px;
    &::before {
        ${name => {
            switch (name) {
                case "heart":
                    return css`
                        content: '\f3dc';
                    `
                case "ellipsis":
                    return css`
                        content: '\f12c';
                        font-size: 16px;
                    `
                case "music":
                    return css`
                        content: '\f156';
                        font-size: 16px;
                    `
                default:
                    return css`
                        content: '\f3db';
                    `
            }
        }}
    }
`

function Emoji({ name })  {
    return (
        <StyledSpan name role="img" aria-label={name} />
    )
}

Emoji.defaultProps = {
    name: "heart-empty"
}

Emoji.propTypes = {
    name: PropTypes.oneOf(["heart","heart-empty","ellipsis","music"])
}

export default Emoji;
