import React from 'react';
import { connect } from 'react-redux';
import { play, selectSong } from '../redux/actions'
import PropTypes from 'prop-types';
import styled from 'styled-components'

import { ReactComponent as Play } from '../icons/play.svg'
import { ReactComponent as Pause } from '../icons/pause.svg'
import { ReactComponent as Speaker } from '../icons/speaker.svg'
import Emoji from './emoji'

const StyledItem =styled.div`
    width: 100%;
    left: 0;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    padding-left: 30px;
    color: ${props => props.selected ? props.theme.primaryColor : 'white'};
    background-color: ${props => props.selected && 'black'};

    * {
        margin-right: 10px;
    }

    #soundIcon {
        margin-bottom: 50px;
    }

    #playIcon {
        display: none;
        margin-bottom: 50px;
    }

    #meta {
        display: flex;
        color: white;
    }

    #explicit {
        color: black;
        background-color: white;
        border-radius: 3px;
    }

    .ellipsis {
        display: none;
        position: absolute;
        right:90px;
        color: white;
        margin-bottom: 50px;
    }

    #date {
        position: absolute;
        right: 20px;
        margin-bottom: 70px;
    }

    &:hover {
        background-color: black;
        color: ${props => props.theme.primaryColor};
        #soundIcon {
            display: none;
        }
        #playIcon {
            display: block;
        }
        .ellipsis {
            display: block;
        }
    }
`

function SongItem({ song, selectedSong, playing, playStop, changeSong })  {
    return (
        <StyledItem selected={song.id===selectedSong} onClick={() => {
            changeSong(song);
            playStop(!playing);
        }}>
            <div id='soundIcon'>
                {song.id === selectedSong && playing ? <Speaker /> : <Emoji name='music' />}
            </div>
            <div id='playIcon'>
                {song.id === selectedSong && playing ? <Pause /> : <Play />}
            </div>
            <div>
                <h3>{song.name}</h3>
                <div id='meta'>
                    <p id='explicit'>{song.explicit && "EXPLICIT"}</p>
                    <p>{song.author} - {song.album}</p>
                </div>
            </div>
            <Emoji name='ellipsis'/>
            <p id='date'>2:30</p>
        </StyledItem>
    )
}

SongItem.defaultProps = {
    song: {
        id: 1, 
        name: "Titulo",
        explicit: true,
        author: "Autor",
        album: "Album",
        duration: new Date(0, 0, 0, 0, 2, 30),
        like: false
    },
    selectedSong: 1,
}

SongItem.propTypes = {
    song: PropTypes.shape({
        id: PropTypes.number,
        name: PropTypes.string,
        explicit: PropTypes.bool,
        author: PropTypes.string,
        album: PropTypes.string,
        duration: PropTypes.instanceOf(Date),
        like: PropTypes.bool
    }),
    selectedSong: PropTypes.number.isRequired,
    playing: PropTypes.bool.isRequired,
    playStop: PropTypes.func.isRequired,
    changeSong: PropTypes.func.isRequired
}

const mapStateToProps = (state) => {
    return {
        playing: state.playing,
        selectedSong: state.selectedSong.id
    }
}

export default connect(mapStateToProps, {
    playStop: play,
    changeSong: selectSong 
})(SongItem);
