import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import { ReactComponent as Play } from '../icons/play.svg'
import { ReactComponent as Speaker } from '../icons/speaker.svg'
import Emoji from './emoji'

import styled from 'styled-components'

const StyledItem =styled.div`
    left: 0;
    width: 30%;
    display: flex;
    align-items: center;
    justify-content: space-between;
`

function SongItem({ song, selectedSong, playing })  {
    return (
        <>
            <StyledItem>
                <div>
                    {song.id==selectedSong 
                        ? playing ? <Speaker /> : <Play />
                        : <Emoji name='music' />
                    }
                </div>
                <h3>{song.name}</h3>
                <Emoji name='ellipsis'/>
            </StyledItem>
            <StyledItem>
                <span>{song.explicit && "Explicit "}</span>
                <p>{song.author} "-" {song.album}</p>
            </StyledItem>
        </>
    )
}

SongItem.defaultProps = {
    song: {id: 1, name: "Titulo", explicit: true, author: "Autor", album: "Album"},
    selectedSong: 1,
}

SongItem.propTypes = {
    song: PropTypes.shape,
    selectedSong: PropTypes.number,
    playing: PropTypes.bool.isRequired
}

const mapStateToProps = (state) => {
    return {
        playing: state.playing
    }
}

export default connect(mapStateToProps)(SongItem);
