import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import { ReactComponent as Play } from '../icons/play.svg'
import { ReactComponent as Speaker } from '../icons/speaker.svg'
import Emoji from './emoji'

import styled from 'styled-components'

const StyledItem =styled.div`
    left: 0;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    padding-left: 30px;
    color: ${props => props.selected ? props.theme.primaryColor : 'white'};

    &:hover {
        background-color: black
    }

    * {
        margin-right: 10px;
    }

    #icon {
        margin-bottom: 50px;
    }

    #meta {
        display: flex;
    }

    #explicit {
        color: black;
        background-color: white;
        border-radius: 3px;
    }

    .ellipsis {
        position: absolute;
        right: 20px;
        color: white;
    }
`

function SongItem({ song, selectedSong, playing })  {
    return (
        <>
            <StyledItem selected={song.id===selectedSong}>
                <div id='icon'>
                    {song.id===selectedSong 
                        ? playing ? <Speaker /> : <Play />
                        : <Emoji name='music' />
                    }
                </div>
                <div>
                    <h3>{song.name}</h3>
                    <div id='meta'>
                        <p id='explicit'>{song.explicit && "EXPLICIT"}</p>
                        <p>{song.author} - {song.album}</p>
                    </div>
                </div>
                <Emoji name='ellipsis'/>
            </StyledItem>
        </>
    )
}

SongItem.defaultProps = {
    song: {id: 1, name: "Titulo", explicit: true, author: "Autor", album: "Album"},
    selectedSong: 1,
}

SongItem.propTypes = {
    song: PropTypes.shape({
        id: PropTypes.number,
        name: PropTypes.string,
        explicit: PropTypes.bool,
        author: PropTypes.string,
        album: PropTypes.string,
        like: PropTypes.bool
    }),
    selectedSong: PropTypes.number,
    playing: PropTypes.bool.isRequired
}

const mapStateToProps = (state) => {
    return {
        playing: state.playing
    }
}

export default connect(mapStateToProps)(SongItem);
