import React from 'react';
import { connect } from 'react-redux';
import { play, selectSong } from '../redux/actions';
import PropTypes from 'prop-types';
import styled from 'styled-components'

import { ReactComponent as Play } from '../icons/play.svg';
import { ReactComponent as Pause } from '../icons/pause.svg';
import { ReactComponent as Speaker } from '../icons/speaker.svg';
import Emoji from './emoji';

const StyledListItem = styled.div`
    width: 100%;
    height: 90px;
    display: flex;
    align-items: end;
    padding: 10px 0 0 30px;
    color: ${props => props.selected ? props.theme.primaryColor : 'white'};
    background-color: ${props => props.selected && 'black'};

    * {
        margin-right: 10px;
    }

    #soundIcon {
        margin-bottom: 50px;
    }

    #playIcon {
        display: none;
        margin-bottom: 50px;
    }

    #title {
        width: 100%;
        height: 75%;
        h3, p {
            margin-top: 2px;
            margin-bottom: 2px;
        }
    }

    #meta {
        display: flex;
        color: white;
    }

    #explicit {
        color: black;
        margin-right: 0;
        background-color: white;
        border-radius: 3px;
    }

    #menu {
        display: none;
        right:90px;
        color: white;
        margin-bottom: 50px;
    }

    #date {
        right: 20px;
        margin-bottom: 70px;
    }

    &:hover {
        background-color: black;
        color: ${props => props.theme.primaryColor};
        #soundIcon {
            display: none;
        }
        #playIcon {
            display: block;
        }
        #menu {
            display: block;
        }
    }
`

const StyledMenu = styled.ul`
    list-style: none;
    li {
        float: right
    }
    //display: ${props => props.open ? 'block' : 'none'};
`

function SongItem({ song, selectedSongId, playing, playStop, changeSong })  {
    return (
        <StyledListItem selected={song.id===selectedSongId} onClick={() => {
            // The song is selected.
            changeSong(song);

            //  We verify if the clicked song was made to the current playing song.
            if (song.id===selectedSongId) {
                playStop(false);
            } else {
                playStop(true);
            }
        }}>
            <div id='soundIcon'>
                {song.id === selectedSongId && playing ? <Speaker /> : <Emoji name='music' />}
            </div>
            <div id='playIcon'>
                {song.id === selectedSongId && playing ? <Pause /> : <Play />}
            </div>
            <div id='title'>
                <h3>{song.name}</h3>
                <div id='meta'>
                    <p id='explicit'>{song.explicit && "EXPLICIT"}</p>
                    <p>{song.author} - {song.album}</p>
                </div>
            </div>
            <div id='menu'>
                <Emoji name='ellipsis'/>
                <StyledMenu>
                    <li>Start Radio</li>
                    <li>Save to your Favorite Songs</li>
                    <li>Add to Queue</li>
                    <li>Add to Playlist</li>
                    <li>Copy Song Link</li>
                </StyledMenu>
            </div>
            <p id='date'>{song.duration.getMinutes()}:{song.duration.getSeconds()}</p>
        </StyledListItem>
    )
}

SongItem.defaultProps = {
    song: {
        id: 1, 
        name: "Titulo",
        explicit: true,
        author: "Autor",
        album: "Album",
        duration: new Date(0, 0, 0, 0, 2, 30),
        like: false
    }
}

SongItem.propTypes = {
    song: PropTypes.shape({
        id: PropTypes.number,
        name: PropTypes.string,
        explicit: PropTypes.bool,
        author: PropTypes.string,
        album: PropTypes.string,
        duration: PropTypes.instanceOf(Date),
        like: PropTypes.bool
    }),
    selectedSongId: PropTypes.number.isRequired,
    playing: PropTypes.bool.isRequired,
    playStop: PropTypes.func.isRequired,
    changeSong: PropTypes.func.isRequired
}

const mapStateToProps = (state) => {
    return {
        playing: state.playing,
        selectedSongId: state.selectedSong.id
    }
}

export default connect(mapStateToProps, {
    playStop: play,
    changeSong: selectSong 
})(SongItem);
